import React,{useState} from "react";

function CreateArea() {
  const[title, setTitle]=useState("");
  const[content, setContent]=useState("");
  const[noteTitle,setNoteTitle]=useState([]);
  const[noteContent,setNoteContent]=useState([]);

  function handleChangeT(event){
    const newValueT=event.target.value;
    setTitle(newValueT);
  }
  function handleChangeC(event){
    const newValueC=event.target.value;
    setContent(newValueC);
  }
  function addItem(){

    setNoteTitle(prevItem => {
    return [...prevItem,title]
    });
    setNoteContent(prevItem => {
      return [...prevItem,content]
      });


  }

  return (
    <div>
      <form >
        <input name="title" placeholder="Title" value={title} onChange={handleChangeT} />
        <textarea name="content" placeholder="Take a note..." rows="3" value={content}  onChange={handleChangeC}/>
        <button onClick={addItem}>Add</button>
      </form>
    </div>
  );
}

export default CreateArea;
