import React,{useState} from "react";

function CreateArea() {
  const[note, setNote]=useState("");

  function handleChangeT(event){
    const newValueT=event.target.value;
    setNote(newValueT);
  }
  function handleChangeC(event){
    const newValueC=event.target.value;
    setNote(newValueC);
  }

  return (
    <div>
      <form >
        <input name="title" placeholder="Title" onChange={handleChangeT} />
        <textarea name="content" placeholder="Take a note..." rows="3"  onChange={handleChangeC}/>
        <button>Add</button>
      </form>
    </div>
  );
}

export default CreateArea;
